import React, { useState, useEffect,useRef ,setData } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";

//清空購物車
const apiClearCarUrl = 'http://localhost:8090/training/ecommerce/MemberController/clearCartGoods';

const ClearCarGoods = () => {

    const navigate = useNavigate();
    // 後端api:清空購物車
    const fetchList3 = async () => {
        const clearGoodsData = await axios.delete(apiClearCarUrl,{ withCredentials: true })
            .then(rs => rs.data)
            .catch(error => { console.log(error); });
            navigate("/SearchGoods");//導入到購買頁面
            setData( e => ({
                ...e,//hooks無法部分更新,加...e為保持原先欄位
                  
                // clearGoodsData:clearGoodsData,

                // customer: sendGoodsData.customer, 
                // orderGoodsList: sendGoodsData.orderGoodsList,
                // queryGoodDataWebPage:queryGoodsData,//因為到時候要清空session購物車 用這個裝來畫頁面

                // queryGoodsData:clearGoodsData,//送出訂單後 購物車要清空
            }));
        
    };

    const clearCartGoods = () => {
        fetchList3();
    };

    return (
        <div>
            {/* 因為要多用途所以這邊沒有名字 */}
            <button onClick={clearCartGoods}>清空購物車</button>    
        </div>
    )
}

export default ClearCarGoods
