import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";

//用於輸入搜尋文字使用
const SearchName = () => {
    const navigate = useNavigate();

    const [data, setData] = useState({
        // 各欄位的初始化
        searchName:'',
        page:1,
        toggle:true,
        // count:0,
      });

      const {searchName,toggle,count} =data ;
    
    // 頁面標頭的商品搜尋
    const changeSearchGoodsName = (event) => {
        setData((prevState) => ({
        ...prevState,
        searchName: event.target.value,
        }));
    };  

    // 送出搜尋
    const clickSearchName = (event) => {
        setData((prevState) => ({
            ...prevState,
            toggle:!toggle,
            // page:1,
            count:count+1,
            }));
        navigate("/SearchGoods",{state:data});
    };  

   

    return (
        <div>

            <input type="text" onBlur={changeSearchGoodsName} placeholder='搜尋商品名稱'/>
            <button onClick={clickSearchName}>搜尋</button>
            <br/>
            測試中:{searchName}
        </div>
    )
}

export default SearchName
