                {/* 登入頁面:沒登入就是MemberLogic,有登入直接導SearchGoods */}
                <Route path="/"
                     element={!isLogin ? (<MemberLogic/>) : (
                      <Navigate to="/SearchGoods" replace={true} />)}/>

                <Route path="/GoodsCreate"
                     element={ isLoggedIn  ? (<GoodsCreate/>) : (
                      <Navigate to="/MemberLogic" replace={true} />)}/>

                
        
                
                {/* <Route path="/GoodsCreate"
                     element={isLogin ? (<GoodsCreate/>) : (
                      <Navigate to="/" replace={true} state={{ from: '/GoodsCreate' }} />
