import React, { useState, useEffect,useRef } from 'react'
import { useNavigate } from "react-router-dom";
import axios from "axios";
import ClearCarGoods from './ClearCarGoods';

//查詢購物車商品
const apiSearchCarUrl = 'http://localhost:8090/training/ecommerce/MemberController/queryCartGoods';

//送出訂單(結帳購物車商品)
const apiUrl2 = 'http://localhost:8090/training/ecommerce/FrontendController/checkoutGoods';


const QueryCarGoods = () => {
    const navigate = useNavigate();

    //填寫收件人資料用
    const inputRefs = useRef([]);
    const creditCardRefs = useRef([]);

    const [data, setData] = useState({
        // 購物車查詢回來的內容
        queryGoodsData: [],
        
        //填寫信用卡用
        creditCard:'',
        effectiveDate:'',
        lastNumber:'',

        sendGoodsData:[],
        // customerResult:'',
        orderGoodsList:[],
        
    });

    const [cusData, setCusData] = useState({ 
        //結帳用的收件人資料
        customer :{ //api要傳入的資料
            cusName:'',
            homeNumber:'',
            mobileNumber:'',
            orderAddr:'',
        },
    });

    // 解構
    const { queryGoodsData,
        // queryGoodDataWebPage,
        // cusName,homeNumber,mobileNumber,orderAddr,
        creditCard,effectiveDate,lastNumber,
        sendGoodsData,
        // customer,
        orderGoodsList } = data;
    // const{cusName,homeNumber,mobileNumber,orderAddr} =customer;    
    const{customer,cusName,homeNumber,mobileNumber,orderAddr} =cusData;    

    // 呼叫後端api:查詢購物車商品
    const fetchList = async () => {
        const queryGoodsData = await axios.get(apiSearchCarUrl, { withCredentials: true })
            .then(rs => rs.data)
            .catch(error => { console.log(error); });

        if (queryGoodsData) { //如果存在
            setData(prevData => ({
                ...prevData,
                queryGoodsData: queryGoodsData,
            }));
        }
    };
    // 呼叫後端api:送出訂單 並DB建立 扣餘額
    const fetchList2 = async () => {
        const params =  { "cusName": cusName, "homeNumber": homeNumber, "mobileNumber": mobileNumber, "orderAddr": orderAddr};

        const sendGoodsData = await axios.post(apiUrl2,{params},{ withCredentials: true })
            .then(rs => rs.data)
            .catch(error => { console.log(error); });
            setData( e => ({
                ...e,//hooks無法部分更新,加...e為保持原先欄位
                // reportData,
                sendGoodsData:sendGoodsData,
                cusData: sendGoodsData.customer, 
                orderGoodsList: sendGoodsData.orderGoodsList,
                // queryGoodDataWebPage:queryGoodsData,//因為到時候要清空session購物車 用這個裝來畫頁面

                queryGoodsData:[],//送出訂單後 購物車要清空
                mergedData:[],
            }));
        
    };
    
    useEffect(() => {
        fetchList(); //畫面一進來需要看到購物車所有內容
    }, []);


    // 將相同商品類加成為一個項目 
    const mergedData = Object.values(//Object.values 內建函式將 queryGoodsData 所有值取出來放入mergedData陣列中
        queryGoodsData.reduce((result, item) => { //reduce累加
            // queryGoodsData內不存在此ID 初始值為改為1 (備註:當初後端傳回來的json quantity為庫存 為了方便 直接覆蓋來使用)
            if (!result[item.goodsID]) {   //result 累加結果 一開始是空物件
                result[item.goodsID] = { ...item, quantity: 1 };
            } else { //若此ID已經存在 代表商品數量要加1
                result[item.goodsID].quantity += 1;
            }
            return result;
        }, {})
    );
    // reduce 接受兩個參數 (total累加,item當前值),0是初始值
    const totalAmount = mergedData.reduce((total, item) => total + item.price * item.quantity, 0);

    const onChangeData = () => {
        if (inputRefs.current[0]) {
        setData((prevState) => ({
            ...prevState,
            // carGoodsItem: carGoodsItem,
            // customer: {
                cusName:inputRefs.current[0].value,
                homeNumber:inputRefs.current[1].value,
                mobileNumber:inputRefs.current[2].value,
                orderAddr:inputRefs.current[3].value,
            // },
            
            })
        ); 
        }    
    };

    const onChangeCreditCard = () => {
        // if(creditCardRefs.current[0].value.length==4){
        //     creditCardRefs.current[1].focus();
        //     if(creditCardRefs.current[1].value.length==4){
        //         creditCardRefs.current[2].focus();
        //         if(creditCardRefs.current[2].value.length==4){
        //             creditCardRefs.current[3].focus();
        //             if(creditCardRefs.current[3].value.length==4){
        //                 creditCardRefs.current[4].focus();
        //                 if(creditCardRefs.current[4].value.length==5){
        //                     creditCardRefs.current[5].focus();
        //                 }
        //             }
        //         }
        //     }
        // }
        
        setData((prevState) => ({
            ...prevState,
            // carGoodsItem: carGoodsItem,
            creditCard:creditCardRefs.current[0].value+creditCardRefs.current[1].value+creditCardRefs.current[2].value+creditCardRefs.current[3].value,
            effectiveDate:creditCardRefs.current[4].value,
            lastNumber:creditCardRefs.current[5].value,
            })
        ); 

    };
    
    // 送出訂單
    const sendOrder = () => {
        // fetchList2();
        navigate("/CheckOutGoods",{state:data});//直接導入到購買頁面 並將訂購人資料傳送過去
    };

    // 清除購物車 直接給claeaCarGoods那邊做掉
    // const clearData = () => {
    //     setData((prevState) => ({
    //         ...prevState,
    //         queryGoodsData:[],
    //         })
    //     ); 

    // };    

    return (
        <div>
            <h3>購物車商品清單</h3>
            {/* <hr /> */}

            {mergedData.length <= 0 && 
            <h5>尚未有購物車商品 請加入商品</h5>
            }

            {/* 有購物車內容才秀出 填寫收件人 &信用卡資料 這些欄位 */}
            {mergedData.length > 0 &&
                <div>
                    <table border={'2'}>
                        <thead>
                            <tr>
                                <th>商品編號</th>
                                <th>商品圖片</th>
                                <th>商品名稱</th>
                                <th>商品價格</th>
                                <th>商品數量</th>
                                <th>商品小計</th>
                            </tr>
                        </thead>

                        <tbody>
                            {mergedData.map((item, index) => (
                                <tr key={item.goodsID}>
                                    <td>{item.goodsID}</td>
                                    <td>
                                        <img
                                            src={`http://localhost:8090/training/goodsImg/${item.imageName}`}
                                            style={{ width: '100px', height: '75px' }}
                                            alt="Goods Image"
                                        />
                                    </td>
                                    <td>{item.goodsName}</td>
                                    <td>{item.price}</td>
                                    <td>{item.quantity}</td>
                                    <td>{item.price * item.quantity}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    結帳總額:{totalAmount}
                    {/* 清除購物車 */}
                   <ClearCarGoods />     

                    <hr/>
                    填寫收件人資料<br/>
                    姓名:<input type="text" ref={el => (inputRefs.current[0] = el)} onChange={onChangeData}/><br/>
                    手機號碼:<input type="text" ref={el => (inputRefs.current[1] = el)} onChange={onChangeData}/><br/>
                    連絡電話:<input type="text" ref={el => (inputRefs.current[2] = el)} onChange={onChangeData}/><br/>
                    收件地址:<input type="text" ref={el => (inputRefs.current[3] = el)} onChange={onChangeData}/><br/>

                    <hr/>     
                    填寫信用卡資料<br/>
                    信用卡號:
                    <input type="number" ref={el => (creditCardRefs.current[0] = el)} onBlur={onChangeCreditCard} style={{ width: '55px' }} />
                    -<input type="number" ref={el => (creditCardRefs.current[1] = el)} onBlur={onChangeCreditCard} style={{ width: '55px' }} />
                    -<input type="number" ref={el => (creditCardRefs.current[2] = el)} onBlur={onChangeCreditCard} style={{ width: '55px' }}/>
                    -<input type="number" ref={el => (creditCardRefs.current[3] = el)} onBlur={onChangeCreditCard} style={{ width: '55px' }}/>
                    <br/>
                    有效日期:
                    {/* ref={el => (creditCardRefs.current[4] = el)} onChange={onChangeCreditCard} */}
                    <input tyep="text" ref={el => (creditCardRefs.current[4] = el)} onBlur={onChangeCreditCard} style={{ width: '80px' }} />(範例格式: 03/25)<br/>
                    背面末三碼:
                    <input tyep="number" ref={el => (creditCardRefs.current[5] = el)} onBlur={onChangeCreditCard} style={{ width: '80px' }}/><br/>
                    <button onClick={sendOrder} >送出訂單</button>
                    
                    
                    <hr/>            
                     這邊只是測試使用:<br/>
                    {customer}            
                    cusName:{cusName}<br/>
                    homeNumber:{homeNumber}<br/>
                    mobileNumber:{mobileNumber}<br/>
                    orderAddr:{orderAddr}<br/>

                    creditCard:{creditCard}<br/>
                    effectiveDate: {effectiveDate}<br/>
                    lastNumber:{lastNumber}<br/>
                      {/*           
                    新增內容為:<pre>{JSON.stringify(customer, null, 2)}</pre>           
                    新增內容為:<pre>{JSON.stringify(orderGoodsList, null, 2)}</pre>            */}


                </div>
            //這個不要不小心刪掉 是判斷式的尾巴 
            }

            


        </div>
    )
}

export default QueryCarGoods
